import { useState } from "react";
import { Link } from 'react-router-dom';
import "../css/careers.css";

export default function Page() {
  return(
    <div>
      <TopBox />
      <TextArea />
      <AllOfferBox />
      <PosSection />
      <Application />
      <Submit />
      <BottomText />
    </div>
  );
}

/* Leaf image */
function LeafIMG() {
  return(
    <img style = {{
      width: 230, 
      height: 805}} 
      src = './Leaf.png' />
  );
}

/* Submit button pop up */
function PopUp(){
  return(
    <div style = {{
      width: 250, 
      height: '100%', 
      padding: 12, 
      background: '#691E06', 
      boxShadow: '0px 8px 16px -4px rgba(26, 26, 26, 0.22)', 
      borderRadius: 8, 
      justifyContent: 'flex-start', 
      alignItems: 'center', 
      gap: 8, 
      display: 'inline-flex'}}>
      <div style = {{
        color: '#E3E3E3', 
        fontSize: 20, 
        background: '#691E06',
        padding: 10,
        wordWrap: 'break-word'}}>Application submitted</div>
    </div>
  );
}

/* Contact us bottom */
function BottomText() {
  return(
    <div style = {{
      width: '90%', 
      height: '100%', 
      padding: 40, 
      justifyContent: 'center', 
      alignItems: 'center', 
      display: 'inline-flex'}}>
      <div style = {{
        justifyContent: 'flex-start', 
        alignItems: 'flex-start', 
        gap: 4, 
        display: 'flex'}}>
        <div style = {{
          color: '#691E06', 
          fontSize: 25, 
          fontWeight: '450', 
          wordWrap: 'break-word'}}>
            To learn more about careers and opportunities,
        </div>
        <div style = {{
          justifyContent: 'flex-start', 
          alignItems: 'flex-start', 
          display: 'flex'}}>
          <Link to = 'https://www.google.com/'>
            <div style = {{
              color: '#BB4D00', 
              fontSize: 25, 
              fontWeight: '450', 
              textDecoration: 'underline', 
              wordWrap: 'break-word'}}>contact us!</div>
          </Link>
        </div>
      </div>
    </div>
  );
}

/* Dropdown menu item */
function DDItem({label, to}) {
  return(
    <Link to = {to}>
      <div style = {{
        width: 150, 
        height: '90%', 
        paddingLeft: 6, 
        paddingRight: 6, 
        borderRadius: 4, 
        background: '#CA5310',
        justifyContent: 'flex-start', 
        alignItems: 'center', 
        display: 'inline-flex'}}>
        <div style = {{
          flex: '1 1 0', 
          height: 32, 
          padding: 6, 
          borderRadius: 8, 
          justifyContent: 'flex-start', 
          alignItems: 'flex-start', 
          gap: 4, 
          background: '#CA5310',
          display: 'flex'}}>
          <div style = {{
            flex: '1 1 0', 
            background: '#CA5310',
            color: 'white', 
            fontSize: 20, 
            fontWeight: '450',
            wordWrap: 'break-word'}}>{label}</div>
        </div>
      </div>
    </Link>
  );
}

/* Entire dropdown menu */
function DDOptions() {
  return(
    <div style = {{
      width: 200, 
      height: '100%', 
      paddingTop: 10, 
      paddingBottom: 10, 
      background: '#CA5310', 
      boxShadow: '0px 4px 6px -2px rgba(26, 26, 26, 0.20)', 
      borderRadius: 12, 
      flexDirection: 'column', 
      justifyContent: 'flex-start', 
      alignItems: 'flex-start', 
      gap: 4, 
      display: 'inline-flex'}}>
        <DDItem label = {'Home'} to = '/Main' />
        <DDItem label = {'Online Order'} to = '/OnlineOrder' />
        <DDItem label = {'Directions'} to = '/Directions' />
        <DDItem label = {'Menu'} to = '/Menu' />
        <DDItem label = {'Contact'} to = '/ContactUs' />
    </div>
  );
}

/* Submit button */
function Submit(){
  const [isVisible, setVisible] = useState(false);

  const submitClick = () => {
    setVisible(true);

  }
  return(
    <div style = {{
      paddingTop: 20,
      paddingLeft: 80,
      paddingBottom: 90}}>
      <button style = {{
        width: 100, 
        height: '100%', 
        paddingLeft: 25, 
        paddingRight: 25, 
        paddingTop: 15, 
        paddingBottom: 15, 
        background: '#8F250C', 
        borderRadius: 12, 
        justifyContent: 'center', 
        alignItems: 'center', 
        gap: 2, 
        display: 'inline-flex'}} onClick = {submitClick}>
        <div style = {{
          background: '#8F250C', 
          color: 'white', 
          fontSize: 17, 
          fontWeight: '550', 
          wordWrap: 'break-word'}}>Submit</div>
          {isVisible && (
            <div style = {{
              position: 'absolute',
              top: 'auto',
              right: 100}}>
              <PopUp />
            </div>
          )}
        </button>
    </div>
  );
}

/* Application section */
function Application(){
  return(
    <div style = {{
      paddingLeft: 40,
      paddingTop: 50
    }}>
      <div style = {{
        width: '90%', 
        height: '100%',
        fontWeight: '700',
        color: '#691E06',
        letterSpacing: -5,
        fontSize: 90,
        fontWeight: 500,
        }}>Apply Now!</div>
      <div style = {{
        width: '90%', 
        height: '100%',
        paddingTop: 20,
        color: '#691E06',
        fontSize: 40
      }}>
        Enter position(s) and attatch your resume to apply.
      </div>
      <div style = {{paddingLeft: 30}}>
        <div style = {{paddingBottom: 20}}>
          <PosTextBox />
          <IndButton choice = {'Full-Time'}/>
          <IndButton choice = {'Part-Time'}/>
          <IndButton choice = {'Seasonal'}/>
        </div>
        <DropBox />
      </div>
    </div>
  );
}

/* Position Textbox */
function PosTextBox() {
  var newEdits = '';

  return(
    <div style = {{
      width: '90%', 
      height: '100%', 
      flexDirection: 'column', 
      justifyContent: 'flex-start', 
      alignItems: 'flex-start', 
      gap: 4,
      display: 'inline-flex'}}>
      <div style = {{
        alignSelf: 'stretch', 
        color: '#691E06', 
        fontSize: 22, 
        fontWeight: '450',
        paddingLeft: 5,
        paddingTop: 20,
        wordWrap: 'break-word'}}>Position(s)</div>
      <input style = {{
        alignSelf: 'stretch', 
        height: 35, 
        paddingLeft: 12, 
        paddingRight: 30, 
        paddingTop: 6, 
        paddingBottom: 6, 
        background: '#FDFDFD', 
        borderRadius: 8, 
        fontSize: 20,
        border: '0.66px #CA5310 solid'}}
        contentEditable
        type = 'text'
        onInput = {(e) => {newEdits = e.target.textContent}} />
    </div>
  );
}

/* Dropbox for files */
function DropBox(){
  const [uploadedFile, setUploadedFile] = useState(null);

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    setUploadedFile(file);
  }

  return(
    <div style = {{
      width: '90%', 
      height: '100%',
      background: '#BB4D00', 
      borderRadius: 8, 
      flexDirection: 'column', 
      justifyContent: 'flex-start', 
      alignItems: 'flex-start', 
      display: 'inline-flex'}}>
      <div style = {{
        alignSelf: 'stretch', 
        height: 150, 
        padding: 32, 
        background: '#BB4D00', 
        borderRadius: 8, 
        border: '0.66px #CA5310 dotted', 
        flexDirection: 'column', 
        justifyContent: 'center', 
        alignItems: 'center', 
        display: 'flex'}}>
        <label htmlFor = "fileInput" style = {{
          paddingLeft: 12, 
          paddingRight: 12, 
          paddingTop: 8, 
          paddingBottom: 8, 
          background: 'white', 
          boxShadow: '0px 1px 0px #E3E3E3 inset', 
          borderRadius: 10, 
          justifyContent: 'center', 
          alignItems: 'center', 
          gap: 2, 
          display: 'inline-flex'}}>
          <div style = {{
            color: '#691E06', 
            fontSize: 18, 
            fontWeight: '550',
            background: '#FFF',
            wordWrap: 'break-word'}}>Add files</div>
          <input
            id="fileInput"
            type="file"
            style={{ display: 'none' }}
            onChange={handleFileChange}
          />
        </label>
      </div>
      {uploadedFile && (
        <div style = {{
          background: '#BB4D00',
          color: '#FFF', 
          width: '90%',
          borderRadius: 8}}>
          Uploaded File: {uploadedFile.name}
        </div>
      )}
    </div>
  );
}

/* Singular button for full/part time */
function IndButton({choice}){
  const [isVisible, setVisible] = useState(false);

  const spotClick = () => {
    setVisible(!isVisible)
  }

  return(
    <div style = {{
      width: '90%', 
      height: '100%', 
      paddingTop: 25,
      paddingLeft: 20,
      justifyContent: 'flex-start', 
      alignItems: 'center', 
      gap: 8, 
      display: 'inline-flex'}}>
      <button style = {{
        width: 20, 
        height: 20, 
        position: 'relative',
        }} onClick = {spotClick}>
        <div style = {{
          width: 16, 
          height: 16, 
          left: 0,
          top: 0, 
          position: 'absolute', 
          background: '#FDFDFD'}} />
      </button>
      <div style = {{
        color: '#691E06', 
        fontSize: 24, 
        fontWeight: '450',
        wordWrap: 'break-word'}}>{choice}</div>
      {isVisible && (
      <button style = {{
        width: 20, 
        height: 20, 
        position: 'absolute',
        }} onClick = {spotClick}>
        <div style = {{
          width: 16, 
          height: 16, 
          left: 0,
          top: 0, 
          position: 'absolute', 
          background: '#CA5310'}} />
      </button>
    )}
    </div>

  );
}

/* Box telling singular position */
function PosBox({posName, posDesc}) {
  return(
    <div style = {{
      width: '90%', 
      height: '100%', 
      flexDirection: 'column', 
      background: '#BB4D00', 
      justifyContent: 'flex-start', 
      alignItems: 'flex-start',
      paddingLeft: 15,
      display: 'inline-flex'}}>
